class Grapefruit extends StatefulWidget {
  const Grapefruit({Key? key}) : super(key: key);

  @override
  State<Grapefruit> createState() => _GrapefruitState();
}

class _GrapefruitState extends State<Grapefruit> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        backgroundColor: Colors.orange,
        title: Text("Grapefruit"),
      ),
      body: Padding(
        padding: const EdgeInsets.only(top: 10),
        child: Column(
          children: [
            Container(
              child: Image(
                image: NetworkImage(
                    'data:image/jpeg;base64,'),
                width: 1100,
                height: 200,
                fit: BoxFit.fill,
              ),
            ),
            Padding(
              padding: const EdgeInsets.all(12.0),
              child: Container(
                  child: Align(
                    alignment: Alignment.bottomLeft,
                    child: Text(
                      "Although grapefruit is mostly water, it is a very healthy food, with high levels of vitamins, minerals and nutrients.A grapefruit supplies about 10% of your daily potassium needs, along with 8% of your requirements for thiamine and folate. You also get additional antioxidant power from lycopene, a natural, nutritious compound found in tomatoes and watermelon. Antioxidants help reduce wear and tear on our cells and may even help prevent serious diseases such as cancer.",
                      style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold),
                    ),
                  )),
            ),
            SizedBox(
              height: 40,
            ),
            Padding(
              padding: const EdgeInsets.all(12.0),
              child: Container(
                  child: Align(
                    alignment: Alignment.bottomLeft,
                    child: Linkify(
                      text:
                      "More Details https://www.hopkinsmedicine.org/health/wellness-and-prevention/grapefruit-benefits",
                      style: TextStyle(fontSize: 20),
                      onOpen: _onOpen,
                    ),
                  )),
            )
          ],
        ),
      ),
    );
  }

  Future<void> _onOpen(LinkableElement link) async {
    if (await canLaunch(link.url)) {
      await launch(link.url);
    } else {
      throw ("cannot open link $link");
    }
  }
}

